<template>
  <a :href="href" target="_blank">
    <img width="30" :src="src" />
  </a>
</template>

<script lang="ts">
import { computed, defineComponent, inject, ref } from "vue";

export default defineComponent({
  name: "HPlexLink",
  props: {
    ratingKey: null,
    width: {
      type: Number,
      default: 30,
    },
  },
  setup(props, { emit }) {
    let url = inject("plexUrl");
    // let token = inject("plexToken");
    const src = ref(
      "data:image/png;base64,"
    );

    let href = computed(() => {
      if (props.ratingKey) {
        return (
          url +
          "/web/index.html#!/server/4fee3a5dd1e7459bf6f31838aa03995a4c212a9a/details?key=%2Flibrary%2Fmetadata%2F" +
          props.ratingKey
        );
      } else {
        return "";
      }
    });

    return {
      src,
      href,
    };
  },
});
</script>

<style lang="less" scoped>
img {
  display: inline;
}
</style>
